sys_script_include.script:
var RestrictStateForP1 = RestrictStateForP1lass.create();
RestrictStateForP1.prototype = {
    initialize: function() {
        var cnt = 0;
        var gr = new GlideRecord('incident');
        gr.addQuery('category', this.getParameter("sysparm_category"));
        gr.query();
        while (gr.next()) {
		cnt ++;
        }
		return cnt;
    },



    type: 'RestrictStateForP1'
};
