sys_script_include.script:
var GetSimilarCategoryIncident = Class.create();
GetSimilarCategoryIncident.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

    GetCategoryIncidents: function() {
        var cnt = 0;
        var gr = new GlideRecord('incident');
        //    gr.addQuery('category', this.getParameter('sysparm_getcategory'));
        var catr = this.getParameter('sysparm_getcategory');
		var asg = this.getParameter('sysparm_asg');
        var genqry = "category="+catr+" AND state="+asg;
		//gs.addInfoMessage(genqry);
		gr.addEncodedQuery(genqry);
        gr.query();
        while (gr.next()) {
            cnt = cnt + 1;
        }
        return cnt;
    },

    type: 'GetSimilarCategoryIncident'
});
